#define AGAIN_1X  0x100
#define DGAIN_1X  0x100
#define PS5270_MaxGain 28

ISP_UINT32 ISP_dgain, s_gain;
//ISP_UINT32 isp_gain;
ISP_UINT32 sensor_gain_set = 4096;

